// Aqui você irá escrever as suas funções de Validação, para verificar se o Formulário foi preenchido corretamente
import { useState } from 'react'
// import './style.scss'
import { Card } from "../src/Card"


function App() {
  // Aqui você irá criar os Estados para manipular os Inputs
  const [nomeProduto, setNomeProduto] = useState('')
  const [posicao, setPosicao] = useState('')
  const [fotoJogador, setFotoJogador] = useState('')
  const [formularioErro, setFormularioErro] = useState(false)
  const [allProducts, setAllProducts] = useState([
      {
          id: 1,
          name: 'NEYMAR JR',
          position: 'Atacante',
          picture: 'http://www.itajuba.mg.gov.br/wp-content/uploads/2020/07/neymar.jpeg'
      },
      {
          id: 2,
          name: 'Daniel Alves',
          position: 'Lateral Direito',
          picture: 'https://img.r7.com/images/2014/06/09/3snp4np4li_1kf6ggiby3_file.jpg'
      },
      {
          id: 3,
          name: 'Marquinhos',
          position: 'Zagueiro',
          picture: 'https://i.pinimg.com/474x/ec/f7/5c/ecf75c1c57c676834b0e663d7d8420a4.jpg'
      }
  ])

  const newProduct = {
      name: 'Playstation 4',
      position: 'Zagueiro',
      picture: 'https://i.pinimg.com/474x/ec/f7/5c/ecf75c1c57c676834b0e663d7d8420a4.jpg'
  }



  function cadastrarProduto(event) {

      event.preventDefault()

      const novoProdutoCadastrado = {
          name: nomeProduto,
          position: posicao,
          picture: fotoJogador
      }

      if (nomeProduto === '' || posicao === '') {

          setFormularioErro(true)

      } else {

          setFormularioErro(false)

          setAllProducts([...allProducts, novoProdutoCadastrado])

          setNomeProduto('')
          posicao('')
          fotoJogador('')

      }

      // console.log(opcao)

  }

  return (

      <main className="component">

          <div className="tittle-wrapper">
              <h1>Seleção Brasileira Copa 2022</h1>
              <h2>Aqui é você quem escala! Escolha os jogadores</h2>
              <p>Seleciona e foto no link https://bit.ly/3Ee2YLA</p>
              {/* <button onClick={addNewProduct}>Adicionar novo produto</button> */}
          </div>

          <form className={formularioErro ? 'form-error' : ''} onSubmit={event => cadastrarProduto(event)}>

              <div>
                  <label htmlFor="nomeProduto">Nome</label>
                  <input id="nomeProduto" type="text" value={nomeProduto} onChange={event => setNomeProduto(event.target.value)} />
              </div>

              <div>
                  <label htmlFor="posicao">Posicão</label>
                  <input id="posicao" type="text" value={posicao} onChange={event => setPosicao(event.target.value)} />
              </div>

              <div>
                  <label htmlFor="fotoJogador">Foto</label>
                  <input id="fotoJogador" type="text" value={fotoJogador} onChange={event => setFotoJogador(event.target.value)} />
              </div>

              <button type='submit'>Convocar Jogador</button>

          </form>

          {
              formularioErro ? (
                  <span>O seu formulário contem erros</span>
              ) : null
          }

          <section className='products'>
              {
                  allProducts.map(
                      product => {
                          return (
                              <Card
                                  productData={product}
                              />
                          )
                      }
                  )
              }
          </section>

      </main>

  )

}

export default App
